<script>
export default {
    name: 'App',
    components: {},
    data() {
        return {
            backToTop: false,
            navList: [
                { name: 'Home', to: '/' },
                { name: 'Movie', to: '/Movie' },
                { name: 'Article', to: '/Article' },
                { name: 'Tool', to: '/ToolList' },
                { name: 'About', to: '/About' }
            ]
        }
    },
    mounted() {
        window.addEventListener('scroll', this.handleScroll)
    },
    beforeDestroy() {
        window.removeEventListener('scroll', this.handleScroll)
    },
    methods: {
        handleScroll() {
            const scrollTop = window.pageYOffset || document.documentElement.scrollTop || document.body.scrollTop
            this.backToTop = scrollTop > 100
        },
        scrollToTop() {
            window.scrollTo({
                top: 0,
                behavior: 'smooth'
            })
        }
    }
}
</script>

